import React, { useState } from 'react';
import '../cssfiles/Customer_Dash.css';
import axios from 'axios';
import {  useNavigate } from 'react-router-dom';


export default function Customer_Dash({setIsLoggedIn}) {
  const navigate =useNavigate();
  const [formData, setFormData] = useState({
    mobileNumber: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const redirectToRegistration = () => {
    window.location.href = "/register";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log(
      "this is your Mobile Number: " + formData.mobileNumber +
      " and your email: " + formData.email +
      " and your password: " + formData.password
    );

    const data = {
      mobileNumber: formData.mobileNumber,
      email: formData.email,
      password: formData.password
    };

    try {
      const response = await axios.post("http://localhost:8080/login", data);
      const token = response.data.token;

      console.log("login successfully , received toekn", token);

      // stotre the jwt in local storage 
      setIsLoggedIn("jwttoken",token);
      setIsLoggedIn(true);
      navigate("/header");
      } catch (error) {

      console.error("Login errror",error);
      if (error.response && error.response.status === 401) {
        setErrorMessage(error.response.data || "Incorrect username or password");
      } else {
        setErrorMessage("An unexpected error occurred. Please try again.");
      }
       setIsLoggedIn(false); // Ensure login state is false on error
    }
  };

  return (
    <div className="login-container">
      <h2 className="text-center mb-4">Customer Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Mobile Number</label>
          <input
            type="text"
            className="form-control"
            name="mobileNumber"
            placeholder="Enter your mobile number"
            value={formData.mobileNumber}
            onChange={handleChange}
            required
          />
        </div>

      

        <div className="form-group mt-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            name="password"
            placeholder="Enter your password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        {/* Display error message if any */}
        {errorMessage && <p className="error-message" style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{errorMessage}</p>}


        <div className="button-group mt-4 d-flex justify-content-between">
          <button type="submit" className="btn btn-primary w-45">
            Login
          </button>
          <button type="button" className="btn btn-success w-45" onClick={redirectToRegistration}>
            Register
          </button>
        </div>
      </form>
    </div>
  );
}
