import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext'; // Auth context

function Navbar() {
  const { authState, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const redirectToHome = () => {
    window.location.href = "/";
  };

  return (
    <div>
      {/* --- Top Logo Bar (optional) --- */}
      <div className="d-flex justify-content-center align-items-center py-2"
        style={{ backgroundColor: '#ffe600', position: 'fixed', width: '100%', top: 0, zIndex: 2000 }}>
        {/* <span className="fw-bold fs-3 px-4 py-2 border border-dark rounded"
          style={{
            letterSpacing: '3px',
            fontFamily: 'monospace',
            color: '#1a1a1a',
            backgroundColor: '#ffffff',
          }}
        >
          WELCOME TO MEEWOO BANK
        </span> */}
      </div>

      {/* --- Bottom Navigation Bar --- */}
      <nav className="navbar bg-black px-3" data-bs-theme="dark"
        style={{ position: 'sticky', top: '52px', left: 0, width: '100%', zIndex: 1000 }}>
        <div className="container-fluid">
          <div className="row w-100">
            {/* Home (always visible) */}
            <div className="col text-center">
              <Link onClick={redirectToHome} className="btn btn-outline-light w-100">Home</Link>
            </div>

            {/* Logged OUT options */}
            {!authState.isLoggedIn && (
              <>
                <div className="col text-center">
                  <Link to="/login" className="btn btn-outline-light w-100">Customer Booth</Link>
                </div>
                <div className="col text-center">
                  <Link to="/employee" className="btn btn-outline-light w-100">Employee Booth</Link>
                </div>
                <div className="col text-center">
                  <Link to="/admin" className="btn btn-outline-light w-100">Admin Booth</Link>
                </div>
                <div className="col text-center">
                  <Link to="/register" className="btn btn-outline-light w-100">Register</Link>
                </div>
              </>
            )}

            {/* ROLE_USER Logged IN */}
            {authState.isLoggedIn && authState.userRole === 'ROLE_USER' && (
              <div className="col text-center">
                <Link to="/header" className="btn btn-outline-light w-100">My Account</Link>
              </div>
            )}

            {/* ROLE_EMPLOYEE Logged IN */}
            {authState.isLoggedIn && authState.userRole === 'ROLE_EMPLOYEE' && (
              <div className="col text-center">
                <Link to="/employee/dashboard" className="btn btn-outline-light w-100">Employee Portal</Link>
              </div>
            )}

            {/* ROLE_ADMIN Logged IN */}
            {authState.isLoggedIn && authState.userRole === 'ROLE_ADMIN' && (
              <div className="col text-center">
                <Link to="/admin/dashboard" className="btn btn-outline-light w-100">Admin Portal</Link>
              </div>
            )}

            {/* Logout for all roles */}
            {authState.isLoggedIn && (
              <div className="col text-center">
                <button onClick={handleLogout} className="btn btn-outline-danger w-100">Logout</button>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
}

export default Navbar;
