import React, { useState } from 'react';
import '../cssfiles/Customer_Dash.css';
import { paste } from '@testing-library/user-event/dist/paste';
import axios from 'axios';

export default function Customer_Dash() {
  const [formData, setFormData] = useState({
    mobileNumber: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const redirectToRegistration=()=>{
    window.location.href="/registration";
  }

  const handleSubmit= async(e)=>{

    e.preventDefault();
    console.log("this is your Mobile Number"+mobileNumber+"and your email:"+email+"and yoru password"+password);

    const data={
      mobileNumber:mobileNumber,
      email:email,
      password:password
    };

    try{
       
      const response = await axios.post("http://localhost:8080/login",data)
      console.log("this is response data " +response.data)
      if(!response.data){
        alert("Invalid ")
      }

    }catch(error){
      console.log(error);

    }
    
  };

  return (
    <div className="login-container">
      <h2 className="text-center mb-4">Customer Login</h2>
      <form onSubmit={handlesubmit}>
        <div className="form-group">
          <label>Mobile Number</label>
          <input
            type="text"
            className="form-control"
            name="mobileNumber"
            placeholder="Enter your mobile number"
            value={formData.mobileNumber}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group mt-3">
          <label>Email Address</label>
          <input
            type="email"
            className="form-control"
            name="email"
            placeholder="Enter your email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group mt-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            name="password"
            placeholder="Enter your password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <div className="button-group mt-4 d-flex justify-content-between">
          <button type="button" className="btn btn-primary w-45">
            Login
          </button>
          <button type="button" className="btn btn-success w-45"  onClick={redirectToRegistration}>
            Register
          </button>
        </div>
      </form>
    </div>
  );
}
