import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Button,
  TextField,
  Typography,
  CircularProgress,
  Container,
  Paper,
  Alert // For better user feedback than window.alert
} from '@mui/material';

const RegistrationForm = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    fathername: '',
    email: '',
    mobileNumber: '',
    address: '',
    pincode: '',
    dob: '',
    password: '',
    confirmPassword: ''
  });

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
    // Clear messages when user starts typing
    if (error) setError('');
    if (successMessage) setSuccessMessage('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    // Basic validation
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match!");
      return;
    }
    // Add other client-side validations as needed (e.g., email format, required fields if not handled by 'required' prop)

    setIsLoading(true);

    // Simulate API call
    setTimeout(() => {
      // In a real app, you would make an API call here and handle success/error based on the response.
      // For example:
      // try {
      //   const response = await axios.post('/api/register', formData);
      //   setSuccessMessage(response.data.message || "Registration successful! Redirecting...");
      //   // Clear form
      //   setFormData({ /* ... all fields to empty strings ... */ });
      //   setTimeout(() => {
      //     navigate('/login');
      //   }, 1500); // Give time for user to see success message
      // } catch (apiError) {
      //   setError(apiError.response?.data?.message || "Registration failed. Please try again.");
      // } finally {
      //   setIsLoading(false);
      // }

      // --- Simulation Start ---
      console.log('Form submitted successfully with data:', formData);
      setSuccessMessage('Registration successful! Redirecting to login...');

      // Clear the form
      setFormData({
        fname: '',
        lname: '',
        fathername: '',
        email: '',
        mobileNumber: '',
        address: '',
        pincode: '',
        dob: '',
        password: '',
        confirmPassword: ''
      });

      setIsLoading(false);

      // Redirect to login page after a short delay to show the success message
      setTimeout(() => {
        navigate('/login');
      }, 1500);
      // --- Simulation End ---

    }, 2000); // Simulate server response time
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ padding: { xs: 2, sm: 4 }, marginTop: 5, marginBottom: 5 }}>
        <Typography variant="h5" align="center" gutterBottom sx={{ mb: 3 }}>
          Create Your Account
        </Typography>

        {successMessage && <Alert severity="success" sx={{ mb: 2 }}>{successMessage}</Alert>}
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <TextField
            fullWidth
            label="First Name"
            name="fname"
            value={formData.fname}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
            autoFocus
          />
          <TextField
            fullWidth
            label="Last Name"
            name="lname"
            value={formData.lname}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Father's Name"
            name="fathername"
            value={formData.fathername}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Email Address"
            name="email"
            type="email"
            value={formData.email}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Mobile Number"
            name="mobileNumber"
            type="tel"
            value={formData.mobileNumber}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Full Address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            margin="normal"
            multiline
            rows={3}
            required
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Pincode"
            name="pincode"
            value={formData.pincode}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Date of Birth"
            name="dob"
            type="date"
            value={formData.dob}
            onChange={handleChange}
            margin="normal"
            required
            InputLabelProps={{ shrink: true }}
            disabled={isLoading}
          />
          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            value={formData.password}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
            error={!!error && error.toLowerCase().includes('password')} // Highlight if error related to password
          />
          <TextField
            fullWidth
            label="Confirm Password"
            name="confirmPassword"
            type="password"
            value={formData.confirmPassword}
            onChange={handleChange}
            margin="normal"
            required
            disabled={isLoading}
            error={!!error && error.toLowerCase().includes('password')}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            sx={{ mt: 3, mb: 2, py: 1.5 /* Make button a bit taller */ }}
            disabled={isLoading}
          >
            {isLoading ? <CircularProgress size={24} color="inherit" /> : 'Register'}
          </Button>

          <Box textAlign="center">
            <Button
              variant="text" // More subtle than a contained button for this link
              onClick={() => navigate('/login')}
              disabled={isLoading}
              sx={{ textTransform: 'none' }} // Keep casing as is
            >
              Already have an account? Login
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default RegistrationForm;
