import React, { useState } from 'react';
import axios from 'axios';
import '../cssfiles/TransferMoney.css'; // Linking the CSS

const TransferMoney = () => {
  const [senderMobileNo, setSenderMobileNo] = useState('');
  const [amount, setAmount] = useState('');
  const [password, setPassword] = useState('');
  const [receiverAccountNo, setReceiverAccountNo] = useState('');
  const [receiverIFSCCode, setReceiverIFSCCode] = useState('');
  const [receiverBranchCode, setReceiverBranchCode] = useState('');
  const [receiverMobileNo, setReceiverMobileNo] = useState('');
  const [message, setMessage] = useState('');

  const handleTransferMoney = async (e) => {
    e.preventDefault();

    const transferData = {
      senderMobileNo,
      amount: parseFloat(amount),
      password,
      receiverAccountNo,
      receiverIFSCCode,
      receiverBranchCode,
      receiverMobileNo
    };

    try {
      const response = await axios.post('http://localhost:8080/transfer', transferData);
      setMessage(response.data); // Assuming backend returns success message
    } catch (error) {
      console.error('Error transferring money:', error);
      setMessage('Error occurred while transferring money');
    }
  };

  return (
    <div className="transfer-container">
      <h2>Transfer Money</h2>
      <form onSubmit={handleTransferMoney}>
        <input
          type="text"
          placeholder="Sender Mobile Number"
          value={senderMobileNo}
          onChange={(e) => setSenderMobileNo(e.target.value)}
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="text"
          placeholder="Receiver Account Number"
          value={receiverAccountNo}
          onChange={(e) => setReceiverAccountNo(e.target.value)}
        />
        <input
          type="text"
          placeholder="Receiver IFSC Code"
          value={receiverIFSCCode}
          onChange={(e) => setReceiverIFSCCode(e.target.value)}
        />
        <input
          type="text"
          placeholder="Receiver Branch Code"
          value={receiverBranchCode}
          onChange={(e) => setReceiverBranchCode(e.target.value)}
        />
        <input
          type="text"
          placeholder="Receiver Mobile Number"
          value={receiverMobileNo}
          onChange={(e) => setReceiverMobileNo(e.target.value)}
        />
        <button type="submit">Transfer Money</button>
      </form>
      {message && <p className="transfer-message">{message}</p>}
    </div>
  );
};

export default TransferMoney;
