// --- src/App.js ---
import React from 'react'; // Keep React import
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from 'react-router-dom'; // Keep needed imports
import { useAuth } from './context/AuthContext'; // <<< Import useAuth hook

// Common Components
import Navbar from './component/common/Navbar.js'; // Verify path

// Page Components (VERIFY THESE PATHS/NAMES MATCH YOUR PROJECT)
import HomePage from './component/pages/Home';          // Assuming Home.js is here
import LoginPage from './component/Page/Customer_Dash'; // Using your existing component file name for Login
import RegistrationPage from './component/Page/Registration'; // Using your existing component file name

// Customer Pages (Protected)
import Header from './component/Page/Header'; // Customer dashboard (Verify path)
import DepositPage from './component/Page/Deposit'; // (Verify path)
import WithdrawPage from './component/Page/Withdraw'; // (Verify path)
import TransferMoney from './component/Page/TransferMoney'; // (Verify path)
import CheckBalancePage from './component/Page/CheckBalance'; // (Verify path)

// Admin Pages (Protected) - **VERIFY THESE PATHS/FILENAMES**
import AdminDashboardPage from './component/Page/Admin/AdminDashboardPage'; // <<< Your target dashboard
import PendingApprovalsPage from './component/Page/Admin/PendingApprovaslPage.js'; // <<< Page for pending list (can be merged into dashboard)

// Employee Pages (Placeholder)
import Employee_Dash from './component/Page/Employee_Dash';
import TransactionHistoryPage from'./component/Page/TransactionHistoryPage';
import 'bootstrap/dist/css/bootstrap.min.css'; 

import JobApplicationForm from './component/Page/JobApplicationForm.js';

import AdminApplicationsListPage from './component/Page/Admin/AdminApplicationsListPage';
import AdminApplicationDetailPage from './component/Page/Admin/AdminApplicationDetailPage';
import EmployeeDashboardPage from './component/Page/Employee/EmployeeDashboardPage';

const ProtectedRoute = ({ allowedRoles }) => {
    const { authState } = useAuth(); // Get auth state from context

    // Optional: Log state for debugging
    // console.log("ProtectedRoute Check:", { isLoggedIn: authState.isLoggedIn, userRole: authState.userRole, allowedRoles });

    if (!authState.isLoggedIn) {
        // Not logged in, redirect to login page
        return <Navigate to="/login" replace />;
    }

    // Check if specific roles are required AND if the user has one of the allowed roles
    if (allowedRoles && allowedRoles.length > 0 && !allowedRoles.includes(authState.userRole)) {
        // Logged in, but doesn't have permission for this specific route
        console.warn(`ProtectedRoute: Access Denied. Route requires roles [${allowedRoles.join(', ')}], user has role '${authState.userRole}'`);
        // Redirect to an "Unauthorized" page
        return <Navigate to="/unauthorized" replace />;
    }

    // If logged in AND (no specific roles required OR user has an allowed role), render the nested routes
    return <Outlet />; // Outlet renders the matched nested child route (e.g., AdminDashboardPage)
};


function App() {
    // Get global auth state from context to control initial redirects if needed
    const { authState } = useAuth();

    return (
        <Router>
            {/* Navbar uses AuthContext internally to show/hide links */}
            <Navbar />
            <Routes>
                {/* --- Public Routes --- */}
                <Route path="/" element={<HomePage />} />
                <Route
                    path="/login"
                    // If already logged in (based on context state), redirect away from login
                    element={authState.isLoggedIn ? <Navigate to="/" replace /> : <LoginPage />}
                />

                <Route path="/register" element={<RegistrationPage />} />
                <Route path="/unauthorized" element={<div style={{ padding: '20px', textAlign: 'center' }}><h2>403 - Unauthorized Access</h2><p>You do not have permission to view this page.</p></div>} />
                <Route path='/apply' element={<JobApplicationForm/>}/>
                {/* --- Customer Routes (Protected - requires ROLE_USER or other relevant roles) --- */}
                <Route element={<ProtectedRoute allowedRoles={['ROLE_USER', 'ROLE_ADMIN', 'ROLE_EMPLOYEE']} />}>
                    <Route path="/header" element={<Header />} /> {/* Assuming Header is customer dashboard */}
                    <Route path="/deposit" element={<DepositPage />} />
                    <Route path="/withdraw" element={<WithdrawPage />} />
                    <Route path="/transfer" element={<TransferMoney />} />
                    <Route path="/history" element={<TransactionHistoryPage />} />
                    <Route path="/check-balance" element={<CheckBalancePage />} />
                    {/* Add other authenticated customer routes here */}
                </Route>


                {/* --- Admin Routes (Protected - requires ROLE_ADMIN) --- */}
                {/* Parent Route applies protection */}
                <Route element={<ProtectedRoute allowedRoles={['ROLE_ADMIN']} />}>
                    {/* Child routes rendered via <Outlet /> */}
                    {/* This route is matched when admin logs in and navigate('/admin/dashboard') is called */}
                    <Route path="/admin/dashboard" element={<AdminDashboardPage />} />
                    {/* This route is for viewing pending requests, potentially linked from the dashboard */}
                    <Route path="/admin/pending" element={<PendingApprovalsPage />} />
                    <Route path="/admin/applications" element={<AdminApplicationsListPage />}   />
                  <Route path="/admin/applications/:appId" element={<AdminApplicationDetailPage />} />
                </Route>             


                {/* --- Employee Routes (Example - Placeholder) --- */}
                {/* Needs protection like admin routes */}
                {/* <Route element={<ProtectedRoute allowedRoles={['ROLE_EMPLOYEE', 'ROLE_ADMIN']} />}>
                    <Route path="/employee/dashboard" element={<Employee_Dash />} />
                </Route> */}
                {/* Placeholder public route for employee - REMOVE LATER */}
                <Route path="/employee" element={<Employee_Dash />} />


                {/* --- REMOVE Old Public /admin route if it exists --- */}
                {/* This conflicts with the protected admin routes */}
                {/* <Route path="/admin" element={<Admin_Dash />} /> */}


                {/* Catch-all for 404 Not Found */}
                <Route path="*" element={<div style={{ padding: '20px', textAlign: 'center' }}><h2>404 - Page Not Found</h2></div>} />

            </Routes>
        </Router>
    );
}

export default App;
