import React from 'react';
import { Box, Button, Typography, Grid, Card } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';

const Header = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear login state
    setIsLoggedin(false);
    // Redirect to login page
    navigate("/login");
  };

  return (
    <Box sx={{ padding: 4, display: 'flex', flexDirection: 'column', height: '100vh' }}>
      {/* Header Section with Logout Button */}
      
      {/* 3D Effect Cards for Navigation */}
      <Grid container spacing={4} justifyContent="center" sx={{ flex: 1 }}>
        <Grid item>
          <Card
            sx={{
              width: 200,
              height: 200,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: '#f5f5f5',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
              borderRadius: '10px',
              transition: 'all 0.3s ease',
              '&:hover': {
                boxShadow: '0 12px 20px rgba(0, 0, 0, 0.2)',
                transform: 'scale(1.05)',
              }
            }}
          >
            <Link to="/deposit" style={{ textDecoration: 'none' }}>
              <Typography variant="h6" sx={{ color: '#333', fontWeight: 'bold' }}>
                Deposit
              </Typography>
            </Link>
          </Card>
        </Grid>
        <Grid item>
          <Card
            sx={{
              width: 200,
              height: 200,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: '#f5f5f5',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
              borderRadius: '10px',
              transition: 'all 0.3s ease',
              '&:hover': {
                boxShadow: '0 12px 20px rgba(0, 0, 0, 0.2)',
                transform: 'scale(1.05)',
              }
            }}
          >
            <Link to="/withdraw" style={{ textDecoration: 'none' }}>
              <Typography variant="h6" sx={{ color: '#333', fontWeight: 'bold' }}>
                Withdraw
              </Typography>
            </Link>
          </Card>
        </Grid>
        <Grid item>
          <Card
            sx={{
              width: 200,
              height: 200,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: '#f5f5f5',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
              borderRadius: '10px',
              transition: 'all 0.3s ease',
              '&:hover': {
                boxShadow: '0 12px 20px rgba(0, 0, 0, 0.2)',
                transform: 'scale(1.05)',
              }
            }}
          >
            <Link to="/transfer" style={{ textDecoration: 'none' }}>
              <Typography variant="h6" sx={{ color: '#333', fontWeight: 'bold' }}>
                Transfer Money
              </Typography>
            </Link>
          </Card>
        </Grid>
        <Grid item>
          <Card
            sx={{
              width: 200,
              height: 200,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: '#f5f5f5',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
              borderRadius: '10px',
              transition: 'all 0.3s ease',
              '&:hover': {
                boxShadow: '0 12px 20px rgba(0, 0, 0, 0.2)',
                transform: 'scale(1.05)',
              }
            }}
          >
            <Link to="/check-balance" style={{ textDecoration: 'none' }}>
              <Typography variant="h6" sx={{ color: '#333', fontWeight: 'bold' }}>
                Check Balance
              </Typography>
            </Link>
          </Card>
        </Grid>
      </Grid>

      {/* Logout Button at the Bottom */}
      <Button 
        variant="contained" 
        color="secondary" 
        onClick={handleLogout} 
        sx={{ marginTop: 'auto' }}
      >
        Logout
      </Button>
    </Box>
  );
};

export default Header;
