// frontend_section/src/App.js
import React, { useState, useEffect } from 'react'; // Import useEffect
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './component/common/Navbar.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './component/pages/Home';
import Customer_Dash from './component/Page/Customer_Dash.js';
import Employee_Dash from './component/Page/Employee_Dash.js'; // Assuming these don't need customer-specific auth yet
import Admin_Dash from './component/Page/Admin_Dash.js' // Assuming these don't need customer-specific auth yet
import Registration from './component/Page/Registration.js';
import Transfer from './component/Page/TransferMoney.js';
import Deposit from './component/Page/Deposit.js';
import Withdraw from './component/Page/Withdraw.js';
import CheckBalance from './component/Page/CheckBalance.js';
import Header from './component/Page/Header.js'; // Dashboard/Menu page after login

function App() {
  // Initialize isLoggedIn based on whether a token exists in localStorage
  const [isLoggedIn , setIsLoggedIn] = useState(!!localStorage.getItem('jwtToken'));

  // Optional: Add an effect to sync isLoggedIn state with localStorage changes
  useEffect(() => {
    const handleStorageChange = () => {
      setIsLoggedIn(!!localStorage.getItem('jwtToken'));
    };

    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);


  // Define a ProtectedRoute component
  const ProtectedRoute = ({ children }) => {
    if (!isLoggedIn) {
      // If not logged in, redirect to the login page
      return <Navigate to="/login" replace />;
    }
    // If logged in, render the requested component
    return children;
  };

  return (
    <Router>
      <Navbar /> {/* Navbar is always visible */}
      <Routes>
        <Route path="/" element={<Home />} />

        {/* Login page - if already logged in, redirect to header */}
        <Route
          path="/login"
          element={isLoggedIn ? <Navigate to="/header" replace /> : <Customer_Dash setIsLoggedIn={setIsLoggedIn} />}
        />

        {/* Registration page - accessible always */}
        <Route path="/register" element={<Registration />} />

        {/* Employee and Admin routes (adjust protection as needed later) */}
        <Route path="/employee" element={<Employee_Dash />} />
        <Route path="/admin" element={<Admin_Dash />} />

        {/* Protected Routes - only accessible when logged in */}
        <Route
          path="/header"
          element={
            <ProtectedRoute>
              <Header setIsLoggedIn={setIsLoggedIn} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/deposit"
          element={
            <ProtectedRoute>
              <Deposit />
            </ProtectedRoute>
          }
        />
        <Route
          path="/transfer"
          element={
            <ProtectedRoute>
              <Transfer />
            </ProtectedRoute>
          }
        />
         <Route
          path="/withdraw"
          element={
            <ProtectedRoute>
              <Withdraw />
            </ProtectedRoute>
          }
        />
         <Route
          path="/check-balance"
          element={
            <ProtectedRoute>
              <CheckBalance />
            </ProtectedRoute>
          }
        />

        {/* Add a catch-all or 404 route if desired */}
        {/* <Route path="*" element={<NotFoundPage />} /> */}

      </Routes>
    </Router>
  );
}

export default App;
