import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext'; // Make sure to import useAuth

function Navbar() {
  const { authState, logout } = useAuth(); // Get auth state and logout function
  const navigate = useNavigate();

  const redirectToHome = () => {
    navigate("/"); // Correct navigate usage
  };

  // Handle logout action
  const handleLogout = () => {
      logout();
      navigate('/login'); // Redirect to login after logout
  };

  return (
    <div>
      {/* Top Logo Section (Optional - Keep as is if you like it) */}
      {/* <div className="d-flex justify-content-center align-items-center py-2" style={{ backgroundColor: '#ffe600', position: 'fixed' }}> */}
        {/* Optional Logo or Title */}
      {/* </div> */}

      {/* Bottom Navigation Section */}
      {/* Adding some padding-top if the fixed logo section is removed or handled differently */}
      <nav className="navbar bg-black px-3" data-bs-theme="dark" style={{ position: 'sticky', top: 0, left: 0, width: '100%', zIndex: 1000 /*, paddingTop: '50px' */ }}>
        <div className="container-fluid">
          <div className="row w-100">

            {/* Home Button */}
            <div className="col text-center">
              <button onClick={redirectToHome} className="btn btn-outline-light w-100">Home</button>
            </div>

            {/* Conditional Links Based on Auth State */}
            {!authState.isLoggedIn && (
              <>
                <div className="col text-center">
                  {/* Changed text for clarity */}
                  <Link to="/login" className="btn btn-outline-light w-100">Customer Login</Link>
                </div>
                {/* Link for anyone to apply */}
                 <div className="col text-center">
                     <Link to="/apply" className="btn btn-outline-info w-100">Apply for Job</Link> {/* Changed style slightly */}
                 </div>
              </>
            )}


            {/* Links for Logged-in ROLE_USER */}
            {authState.isLoggedIn && authState.userRole === 'ROLE_USER' && (
              <div className="col text-center">
                <Link to="/header" className="btn btn-outline-light w-100">My Account</Link>
              </div>
            )}

            {/* Links for Logged-in ROLE_EMPLOYEE - THIS SECTION IS ALREADY CORRECT */}
            {authState.isLoggedIn && authState.userRole === 'ROLE_EMPLOYEE' && (
              <div className="col text-center">
                <Link to="/employee/dashboard" className="btn btn-outline-light w-100">Employee Portal</Link>
              </div>
            )}

            {/* Links for Logged-in ROLE_ADMIN */}
            {authState.isLoggedIn && authState.userRole === 'ROLE_ADMIN' && (
              <div className="col text-center">
                 {/* ✅ FIXED TEXT: Changed "Customer Portal" to "Admin Portal" */}
                <Link to="/admin/dashboard" className="btn btn-outline-light w-100">Admin Portal</Link>
              </div>
            )}

            {/* Logout Button */}
            {authState.isLoggedIn && (
              <div className="col text-center">
                 {/* Using handleLogout function */}
                <button onClick={handleLogout} className="btn btn-outline-danger w-100">Logout</button>
              </div>
            )}

          </div>
        </div>
      </nav>
    </div>
  );
}

export default Navbar;
