import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './component/common/Navbar.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './component/pages/Home';
import Customer_Dash from './component/Page/Customer_Dash.js';
import Employee_Dash from './component/Page/Employee_Dash.js';
import Admin_Dash from './component/Page/Admin_Dash.js';

// Function to render the Home component
function renderHome() {
  return <Home />;
}

// Function to render the Customer Dashboard component
function renderCustomerDash() {
  return <Customer_Dash />;
}

// Function to render the Employee Dashboard component
function renderEmployeeDash() {
  return <Employee_Dash />;
}

// Function to render the Admin Dashboard component
function renderAdminDash() {
  return <Admin_Dash />;
}

// Main App Component
function App() {
  const location = useLocation(); // Get the current route

  return (
    <Router>
      {/* Conditionally render the Navbar based on the current route */}
      {location.pathname !== '/customer' && <Navbar />}

      <Routes>
        <Route path="/" element={renderHome()} />
        <Route path="/customer" element={renderCustomerDash()} />
        <Route path="/employee" element={renderEmployeeDash()} />
        <Route path="/admin" element={renderAdminDash()} />
      </Routes>
    </Router>
  );
}

export default App;
