import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';

const Header = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear login state
    setIsLoggedIn(false);
    // Redirect to login page
    navigate("/login");
  };

  return (
    <AppBar 
      position="static"
      sx={{
        backgroundColor: '#333', // Dark background for the navbar
        boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)', // 3D shadow effect
        borderRadius: '10px', // Optional rounded corners
        padding: 2,
      }}
    >
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        {/* Navbar Title */}
        <Typography variant="h6" sx={{ fontWeight: 'bold', textTransform: 'uppercase', letterSpacing: 2 }}>
          Your Personal Page
        </Typography>
        
        {/* Links Section */}
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button 
            component={Link} 
            to="/deposit" 
            sx={{
              color: 'white', 
              '&:hover': { backgroundColor: '#555', transform: 'scale(1.05)', transition: 'all 0.3s ease' }, 
              padding: '6px 12px',
            }}
          >
            Deposit
          </Button>
          <Button 
            component={Link} 
            to="/withdraw" 
            sx={{
              color: 'white', 
              '&:hover': { backgroundColor: '#555', transform: 'scale(1.05)', transition: 'all 0.3s ease' }, 
              padding: '6px 12px',
            }}
          >
            Withdraw
          </Button>
          <Button 
            component={Link} 
            to="/transfer" 
            sx={{
              color: 'white', 
              '&:hover': { backgroundColor: '#555', transform: 'scale(1.05)', transition: 'all 0.3s ease' }, 
              padding: '6px 12px',
            }}
          >
            Transfer Money
          </Button>
          <Button 
            component={Link} 
            to="/check-balance" 
            sx={{
              color: 'white', 
              '&:hover': { backgroundColor: '#555', transform: 'scale(1.05)', transition: 'all 0.3s ease' }, 
              padding: '6px 12px',
            }}
          >
            Check Balance
          </Button>
        </Box>

        {/* Logout Button */}
        <Button 
          variant="contained" 
          color="error" 
          sx={{
            marginLeft: 'auto', // To push the button to the right side
            '&:hover': { backgroundColor: '#d32f2f', transform: 'scale(1.05)', transition: 'all 0.3s ease' }, 
            padding: '6px 12px',
          }} 
          onClick={handleLogout}
        >
          Logout
        </Button>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
