import React, { useEffect, useState } from "react";
// import "./FeaturesSection.css";

const featuresData = [
  {
    title: "24/7 Support",
    description: "Our bank provides continuous customer support, ensuring help is always available whenever needed.",
  },
  {
    title: "Easy Online Banking",
    description: "Manage your accounts easily online, with quick access to balance checks, transfers, and other services.",
  },
  {
    title: "Secure Transactions",
    description: "We utilize advanced encryption techniques to secure all your transactions, keeping your financial data safe.",
  },
  {
    title: "Low Interest Loans",
    description: "Get affordable loan options with minimal paperwork and quick approvals.",
  },
];

const FeaturesSection = () => {
  const [currentFeature, setCurrentFeature] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentFeature((prevFeature) => (prevFeature + 1) % featuresData.length);
    }, 3000); // Change feature every 3 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <section className="features-section">
        <div className="section-title">
          Bank <br /> Features & <br /> Benefits
        </div>

        <div className="slider-container">
          <div className="feature-card slide-in">
            <h3>{featuresData[currentFeature].title}</h3>
            <p>{featuresData[currentFeature].description}</p>
          </div>
        </div>
      </section>

      {/* Footer Section */}
      <footer className="footer-section">
        <div className="footer-container">
          <div className="footer-about">
            <h4>About</h4>
            <p>We are a trusted financial institution dedicated to providing the best banking experience.</p>
          </div>

          <div className="footer-contact">
            <h4>Contact</h4>
            <p>Email: support@ourbank.com</p>
            <p>Phone: +91 12345 67890</p>
          </div>
        </div>
      </footer>
    </>
  );
};

export default FeaturesSection;
