import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function CustomerDashboard() {
  const [customer, setCustomer] = useState(null);
  const [error, setError] = useState(null);

  // Fetch customer details on page load
  useEffect(() => {
    // Replace '1' with the actual customer ID after login (can be from localStorage or authentication)
    const customerId = 1;

    // Fetch customer data from the backend
    const fetchCustomerDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/customer/${customerId}`);
        setCustomer(response.data);
      } catch (err) {
        console.error("Error fetching customer data:", err);
        setError("Unable to fetch customer data.");
      }
    };

    fetchCustomerDetails();
  }, []);  // Empty dependency array means it will run once when the component loads

  return (
    <div className="container mt-4">
      <h2 className="text-center">Welcome to Customer Dashboard</h2>
      {error && <p className="text-danger">{error}</p>} {/* Display error message */}
      
      {customer ? (
        <div className="card p-4 mt-3 shadow">
          <h4>Customer Details</h4>
          <p><strong>Name:</strong> {customer.firstName} {customer.lastName}</p>
          <p><strong>Email:</strong> {customer.email}</p>
          <p><strong>Phone:</strong> {customer.contactNumber}</p>
          {/* Display other customer details */}
          
          <h5 className="mt-3">Account Info</h5>
          <p><strong>Account Type:</strong> {customer.accountType}</p>
          <p><strong>Balance:</strong> ${customer.balance}</p>

          <h5 className="mt-3">Recent Transactions</h5>
          <ul>
            {customer.transactions?.map((transaction, index) => (
              <li key={index}>
                {transaction.description} - ${transaction.amount} on {transaction.date}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading customer data...</p>  // Show loading text until data is fetched
      )}
    </div>
  );
}
