import React, { useState } from 'react';
import '../cssfiles/Customer_Dash.css';
import axios from 'axios';
import {  useNavigate } from 'react-router-dom';



export default function Customer_Dash({setIsLoggedIn}) {
  const [errorMessage, setErrorMessage] = useState(''); 
  const navigate =useNavigate();
  const [formData, setFormData] = useState({
    mobileNumber: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const redirectToRegistration = () => {
    window.location.href = "/register";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log(
      "this is your Mobile Number: " + formData.mobileNumber +
      " and your email: " + formData.email +
      " and your password: " + formData.password
    );

    const data = {
      mobileNumber: formData.mobileNumber,
      password: formData.password
    };

    try {
      const response = await axios.post("http://localhost:8080/login", data);
      const token = response.data.token;

      console.log("login successfully , received toekn", token);

      // stotre the jwt in local storage 
      setIsLoggedIn("jwttoken",token);
      setIsLoggedIn(true);
      navigate("/header");
      } catch (error) {

      console.error("Login errror",error);
      if (error.response && error.response.status === 401) {
        setErrorMessage(error.response.data || "Incorrect username or password");
      } else {
        setErrorMessage("An unexpected error occurred. Please try again.");
      }
       setIsLoggedIn(false); // Ensure login state is false on error
    }
  };

  return (
    <form>
      <h2>Login</h2>
      {errorMessage && <p style={styles.error}>{errorMessage}</p>}
            <div style={styles.inputGroup}>
                <label htmlFor="identifier" style={styles.label}>Email:</label>
                <input
                    type="text"
                    id="identifier"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required
                    style={styles.input}
                    disabled={isLoading}
                />
            </div>
            <div style={styles.inputGroup}>
                <label htmlFor="password" style={styles.label}>Password:</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    style={styles.input}
                    disabled={isLoading}
                />
            </div>
            <button type="submit" disabled={isLoading} style={styles.button}>
                {isLoading ? 'Logging in...' : 'Login'}
            </button>
    </form>
  );
}


// Basic inline styles (consider using CSS Modules or a UI library)
const styles = {
    form: {
        display: 'flex',
        flexDirection: 'column',
        maxWidth: '300px',
        margin: '2rem auto',
        padding: '2rem',
        border: '1px solid #ccc',
        borderRadius: '5px',
    },
    inputGroup: {
        marginBottom: '1rem',
    },
    label: {
        display: 'block',
        marginBottom: '0.5rem',
    },
    input: {
        width: '100%',
        padding: '0.5rem',
        boxSizing: 'border-box', // Include padding in width
    },
    button: {
        padding: '0.7rem',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '3px',
        cursor: 'pointer',
    },
    error: {
        color: 'red',
        marginBottom: '1rem',
        textAlign: 'center',
    },
};
