// CheckBalance.jsx
import React, { useState } from 'react';
import axios from 'axios';
import '../cssfiles/CheckBalance.css'; // CSS file (black and red)

const CheckBalance = () => {
  const [mobileNo, setMobileNo] = useState('');
  const [password, setPassword] = useState('');
  const [balance, setBalance] = useState('');
  const [message, setMessage] = useState('');

  const handleCheckBalance = async (e) => {
    e.preventDefault();

    const checkBalanceData = { mobileNo, password };

    try {
      const response = await axios.post('http://localhost:8080/checkBalance', checkBalanceData);

      if (response.data.status === 'success') {
        setBalance(response.data.balance);
        setMessage('Balance fetched successfully!');
      } else {
        setBalance('');
        setMessage(response.data.message); // show "Incorrect password" or "Customer not found"
      }
    } catch (error) {
      console.error('Error checking balance:', error);
      setBalance('');
      setMessage('Error occurred while checking balance');
    }
  };

  return (
    <div className="check-balance-container">
      <h2>Check Balance</h2>
      <form onSubmit={handleCheckBalance}>
        <input
          type="text"
          placeholder="Enter Your Registered Mobile No"
          value={mobileNo}
          onChange={(e) => setMobileNo(e.target.value)}
        />
        <input
          type="password"
          placeholder="Enter Your Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Check Balance</button>
      </form>

      {balance && (
        <p className="balance-text">
          Your Balance: â‚¹{balance}
        </p>
      )}

      {message && (
        <p className={`message-text ${balance ? 'success' : 'error'}`}>
          {message}
        </p>
      )}
    </div>
  );
};

export default CheckBalance;
