import React, { useState } from 'react';
import { transferMoney } from './Api.js';
import '../'

const TransferMoney = () => {
  const [senderMobileNo, setSenderMobileNo] = useState('');
  const [password, setPassword] = useState('');
  const [receiverMobileNo, setReceiverMobileNo] = useState('');
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');

  const handleTransfer = async (e) => {
    e.preventDefault();

    const transferData = { senderMobileNo, password, receiverMobileNo, amount: parseFloat(amount) };
    const result = await transferMoney(transferData);
    setMessage(result);
  };

  return (
    <div>
      <h2>Transfer Money</h2>
      <form onSubmit={handleTransfer}>
        <input
          type="text"
          placeholder="Sender Mobile Number"
          value={senderMobileNo}
          onChange={(e) => setSenderMobileNo(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="text"
          placeholder="Receiver Mobile Number"
          value={receiverMobileNo}
          onChange={(e) => setReceiverMobileNo(e.target.value)}
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <button type="submit">Transfer</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default TransferMoney;
