// --- src/component/Page/Header.js ---
import React, { useState } from 'react';
import axios from 'axios';
// Import MUI components - ADD IconButton HERE
import {
    Box, Button, Typography, Grid, Card, Container, Paper,
    CircularProgress, Alert, IconButton // <<< ADD IconButton
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

// Import Icons
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SwapHorizIcon from '@mui/icons-material/SwapHoriz';
import DownloadIcon from '@mui/icons-material/Download';
import LogoutIcon from '@mui/icons-material/Logout';
import RefreshIcon from '@mui/icons-material/Refresh';

// Backend API URL
const API_BASE_URL = 'http://localhost:8080';

const Header = () => {
    const navigate = useNavigate();
    const { logout, authState } = useAuth();

    // State for Balance Check
    const [balance, setBalance] = useState(null);
    const [isBalanceLoading, setIsBalanceLoading] = useState(false);
    const [balanceError, setBalanceError] = useState('');

    const handleLogout = () => {
        logout();
        navigate("/login");
    };

    // Function to fetch balance
    const handleCheckBalance = async () => {
        setIsBalanceLoading(true);
        setBalance(null);
        setBalanceError('');

        try {
            const response = await axios.get(`${API_BASE_URL}/check-balance`, {
                withCredentials: true
            });
            if (response.data?.success === true && response.data?.balance !== undefined) {
                setBalance(response.data.balance);
                setBalanceError('');
            } else {
                setBalanceError(response.data?.message || "Failed to retrieve balance.");
                setBalance(null);
            }
        } catch (error) {
            console.error('Error checking balance:', error);
            setBalance(null);
            if (error.response) {
                 const errorData = error.response.data;
                 setBalanceError(errorData?.message || `Error: ${error.response.status}. Please ensure you are logged in.`);
            } else if (error.request) {
                setBalanceError("Network error checking balance.");
            } else {
                setBalanceError("An unexpected error occurred while checking balance.");
            }
        } finally {
            setIsBalanceLoading(false);
        }
    };

    // Function to handle download request (Placeholder)
     const handleDownloadHistory = async () => {
         alert('Download Transaction History - Backend endpoint not implemented yet.');
     };


    return (
        <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
             <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
                 <Typography variant="h4" component="h1" gutterBottom align="center">
                    My Account Dashboard
                 </Typography>
                 <Typography variant="body1" paragraph align="center">
                     Welcome, Customer {authState.customerId}!
                 </Typography>

                 {/* --- Display Balance Section --- */}
                 <Box sx={{ my: 2, p: 2, border: '1px solid', borderColor: 'divider', borderRadius: 1, textAlign: 'center' }}>
                     <Typography variant="h6" component="p" sx={{ display: 'inline-block', mr: 2 }}>
                        Current Balance:
                     </Typography>
                     {isBalanceLoading ? (
                         <CircularProgress size={24} sx={{ verticalAlign: 'middle' }} />
                     ) : balance !== null ? (
                         <Typography variant="h6" component="span" color="primary" sx={{ fontWeight: 'bold', verticalAlign: 'middle' }}>
                             ₹{balance}
                         </Typography>
                     ) : (
                         <Typography variant="h6" component="span" color="text.secondary" sx={{ fontStyle: 'italic', verticalAlign: 'middle' }}>
                             Click refresh to view
                         </Typography>
                     )}
                     {/* --- This IconButton was causing the error --- */}
                     <IconButton onClick={handleCheckBalance} disabled={isBalanceLoading} size="small" sx={{ ml: 1, verticalAlign: 'middle' }} color="primary">
                         <RefreshIcon />
                     </IconButton>
                     {/* --- End IconButton --- */}
                     {balanceError && (
                         <Alert severity="error" sx={{ width: '100%', mt: 1, textAlign: 'left' }}>
                             {balanceError}
                         </Alert>
                    )}
                 </Box>
                 {/* --- End Display Balance Section --- */}

             </Paper>

            {/* --- Action Cards --- */}
            <Grid container spacing={3} justifyContent="center">

                 {/* Card 1: Transfer Money */}
                 <Grid item xs={12} sm={6} md={5}>
                      <Card sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 3, height: '100%', '&:hover': { boxShadow: 6 } }}>
                         <SwapHorizIcon color="secondary" sx={{ fontSize: 40, mb: 1 }} />
                         <Typography variant="h6" gutterBottom>Transfer Money</Typography>
                         <Button component={Link} to="/transfer" variant="contained" color="secondary">
                             Go to Transfer
                         </Button>
                     </Card>
                 </Grid>

                  {/* Card 2: Download History */}
                  <Grid item xs={12} sm={6} md={5}>
                      <Card sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 3, height: '100%', '&:hover': { boxShadow: 6 } }}>
                         <DownloadIcon sx={{ fontSize: 40, mb: 1, color: 'success.main' }} />
                         <Typography variant="h6" gutterBottom>Transactions</Typography>
                         <Button onClick={handleDownloadHistory} variant="contained" color="success">
                             Download History
                         </Button>
                     </Card>
                  </Grid>
            </Grid>
        </Container>
    );
};

export default Header;
