// --- Create file: src/component/Page/Admin/AdminApplicationsListPage.js ---
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Link as RouterLink, useNavigate } from 'react-router-dom'; // Use RouterLink

// Import MUI components
import {
    Container, Typography, Box, Button, Paper, Grid, Tooltip, Chip, IconButton,
    TableContainer, Table, TableHead, TableRow, TableCell, TableBody,
    CircularProgress, Alert, Snackbar, Link as MuiLink // Import MuiLink
} from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import VisibilityIcon from '@mui/icons-material/Visibility'; // Icon for view details

const API_BASE_URL = 'http://localhost:8080';

// Helper function to format date/time
const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return 'N/A';
    try {
        const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
        return new Intl.DateTimeFormat('en-US', options).format(new Date(dateTimeString));
    } catch (e) {
        return 'Invalid Date';
    }
};

// Helper function for status chips
const getStatusChip = (status) => {
    let color = 'default';
    switch (status) {
        case 'PENDING': color = 'warning'; break;
        case 'UNDER_REVIEW': color = 'info'; break;
        case 'INTERVIEW_SCHEDULED': color = 'primary'; break;
        case 'HIRED': color = 'success'; break;
        case 'REJECTED': color = 'error'; break;
        default: color = 'default';
    }
    // Use ?? 'UNKNOWN' for null safety on status
    return <Chip label={status?.replace('_', ' ') ?? 'UNKNOWN'} color={color} size="small" />;
};


export default function AdminApplicationsListPage() {
    const [applications, setApplications] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const fetchApplications = useCallback(async () => {
        setIsLoading(true);
        setError('');
        try {
            const response = await axios.get(`${API_BASE_URL}/admin/applications`, {
                withCredentials: true
            });
            setApplications(response.data || []);
        } catch (err) {
            console.error("Error fetching applications:", err);
            setError(err.response?.data?.message || err.message || 'Failed to fetch job applications.');
            setApplications([]);
        } finally {
            setIsLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchApplications();
    }, [fetchApplications]);

    const handleViewDetails = (appId) => {
        navigate(`/admin/applications/${appId}`);
    };

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <Paper elevation={3} sx={{ p: { xs: 2, md: 3 } }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                    <Typography variant="h5" component="h2">
                        Job Applications
                    </Typography>
                    <Tooltip title="Refresh List">
                        <span>
                            <IconButton onClick={fetchApplications} color="primary" disabled={isLoading}>
                                <RefreshIcon />
                            </IconButton>
                        </span>
                    </Tooltip>
                </Box>

                {isLoading && (
                    <Box sx={{ display: 'flex', justifyContent: 'center', my: 5 }}>
                        <CircularProgress />
                    </Box>
                )}

                {!isLoading && error && (
                    <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>
                )}

                {!isLoading && !error && (
                    <TableContainer component={Paper} elevation={1}>
                        <Table stickyHeader aria-label="job applications table">
                            <TableHead>
                                <TableRow sx={{ '& th': { fontWeight: 'bold', backgroundColor: 'grey.200' } }}> {/* Added background to header */}
                                    <TableCell>Applicant Name</TableCell>
                                    <TableCell>Email</TableCell>
                                    <TableCell>Phone</TableCell>
                                    <TableCell>Desired Role</TableCell>
                                    <TableCell>Applied On</TableCell>
                                    <TableCell align="center">Status</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {applications.length === 0 ? (
                                    <TableRow>
                                        <TableCell colSpan={7} align="center" sx={{ py: 3 }}>No job applications found.</TableCell>
                                    </TableRow>
                                ) : (
                                    applications.map((app) => (
                                        <TableRow hover key={app.id} sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                                            <TableCell>{`${app.applicantFirstName || ''} ${app.applicantLastName || ''}`.trim()}</TableCell>
                                            <TableCell>{app.applicantEmail}</TableCell>
                                            <TableCell>{app.applicantPhone}</TableCell>
                                            <TableCell>{app.desiredRole || 'N/A'}</TableCell>
                                            <TableCell>{formatDateTime(app.applicationDate)}</TableCell>
                                            <TableCell align="center">{getStatusChip(app.status)}</TableCell>
                                            <TableCell align="center">
                                                <Tooltip title="View Details & Actions">
                                                    <IconButton
                                                        color="primary"
                                                        size="small"
                                                        onClick={() => handleViewDetails(app.id)}
                                                    >
                                                        <VisibilityIcon />
                                                    </IconButton>
                                                </Tooltip>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                )}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}
            </Paper>
        </Container>
    );
}
