// --- src/App.js ---
import React from 'react'; // Keep React import
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from 'react-router-dom'; // Keep needed imports
import { useAuth } from './context/AuthContext'; // <<< Import useAuth hook

// Common Components
import Navbar from './component/common/Navbar.js'; // Verify path

// --- Page Components ---

// Public Pages
import HomePage from './component/pages/Home';          // Assuming Home.js is here
import LoginPage from './component/Page/Customer_Dash'; // Using your existing component file name for Login
import RegistrationPage from './component/Page/Registration'; // Using your existing component file name
import JobApplicationForm from './component/Page/JobApplicationForm'; // *** NEW: Public Job Application Form ***

// Customer Pages (Protected - ROLE_USER)
import Header from './component/Page/Header'; // Customer dashboard (Verify path)
// import DepositPage from './component/Page/Deposit'; // Decide if customer self-deposit exists
// import WithdrawPage from './component/Page/Withdraw'; // Decide if customer self-withdraw exists
import TransferMoney from './component/Page/TransferMoney'; // (Verify path)
import CheckBalancePage from './component/Page/CheckBalance'; // (Verify path)
import TransactionHistoryPage from'./component/Page/TransactionHistoryPage'; // (Verify path)

// Admin Pages (Protected - ROLE_ADMIN)
import AdminDashboardPage from './component/Page/Admin/AdminDashboardPage'; // <<< Your admin hub
import PendingApprovalsPage from './component/Page/Admin/PendingApprovaslPage.js'; // <<< For pending CUSTOMERS
import AdminApplicationsListPage from './component/Page/Admin/AdminApplicationsListPage'; // *** NEW: List job applications ***
import AdminApplicationDetailPage from './component/Page/Admin/AdminApplicationDetailPage'; // *** NEW: Detail/Action for job apps ***

// Employee Pages (Protected - ROLE_EMPLOYEE, ROLE_ADMIN)
// import Employee_Dash from './component/Page/Employee_Dash'; // Replace with specific pages below
import EmployeeDashboardPage from './component/Page/Employee/EmployeeDashboardPage';     // *** NEW: Employee Hub ***
import EmployeeDepositPage from './component/Page/Employee/EmployeeDepositPage';       // *** NEW: Employee performs deposit ***
import EmployeeWithdrawalPage from './component/Page/Employee/EmployeeWithdrawalPage';    // *** NEW: Employee performs withdrawal ***
import EmployeeCheckBalancePage from './component/Page/Employee/EmployeeCheckBalancePage';  // *** NEW: Employee checks customer balance ***
import EmployeeDownloadHistoryPage from './component/Page/Employee/EmployeeDownloadHistoryPage'; // *** NEW: Employee downloads customer history ***

// Styles
import 'bootstrap/dist/css/bootstrap.min.css'; // Keep if using bootstrap elsewhere


// --- ProtectedRoute Component (Keep As Is) ---
// This component correctly checks login status and roles based on AuthContext.
const ProtectedRoute = ({ allowedRoles }) => {
    const { authState } = useAuth();
    // console.log("ProtectedRoute Check:", { isLoggedIn: authState.isLoggedIn, userRole: authState.userRole, allowedRoles });
    if (!authState.isLoggedIn) {
        return <Navigate to="/login" replace />;
    }
    if (allowedRoles && allowedRoles.length > 0 && !allowedRoles.includes(authState.userRole)) {
        // console.warn(`ProtectedRoute: Access Denied. Route requires [${allowedRoles.join(', ')}], user has '${authState.userRole}'`);
        return <Navigate to="/unauthorized" replace />;
    }
    return <Outlet />; // Renders the nested child route if authorized
};


function App() {
    const { authState } = useAuth();

    return (
        <Router>
            {/* Navbar is always visible and uses AuthContext internally */}
            <Navbar />

            {/* Main Content Area with Routes */}
            <Routes>

                {/* ======================================== */}
                {/*           Public Routes                  */}
                {/* Accessible to everyone (logged out/in) */}
                {/* ======================================== */}
                <Route path="/" element={<HomePage />} />
                <Route
                    path="/login"
                    // Redirect away from login if already logged in
                    element={authState.isLoggedIn ? <Navigate to="/" replace /> : <LoginPage />}
                />
                <Route path="/register" element={<RegistrationPage />} />
                {/* *** NEW Public Route for Job Applications *** */}
                <Route path="/apply" element={<JobApplicationForm />} />
                {/* Generic Unauthorized Page */}
                <Route path="/unauthorized" element={<div style={{ padding: '40px', textAlign: 'center' }}><h2>403 - Unauthorized Access</h2><p>You do not have permission to view this page.</p></div>} />


                {/* ======================================== */}
                {/*          Customer Routes                 */}
                {/* Requires ROLE_USER                      */}
                {/* ======================================== */}
                {/* This ProtectedRoute block ensures only users with ROLE_USER can access these */}
                <Route element={<ProtectedRoute allowedRoles={['ROLE_USER']} />}>
                    {/* Customer Dashboard */}
                    <Route path="/header" element={<Header />} />
                    {/* Customer actions on their OWN account */}
                    <Route path="/transfer" element={<TransferMoney />} />
                    <Route path="/history" element={<TransactionHistoryPage />} />
                    <Route path="/check-balance" element={<CheckBalancePage />} />
                    {/* Add other ROLE_USER specific routes here */}
                     {/* Example: If customers can self-deposit/withdraw directly */}
                     {/* <Route path="/deposit" element={<DepositPage />} /> */}
                     {/* <Route path="/withdraw" element={<WithdrawPage />} /> */}
                </Route>


                {/* ======================================== */}
                {/*             Admin Routes                 */}
                {/* Requires ROLE_ADMIN                     */}
                {/* ======================================== */}
                {/* This ProtectedRoute block ensures only users with ROLE_ADMIN can access these */}
                <Route element={<ProtectedRoute allowedRoles={['ROLE_ADMIN']} />}>
                    {/* Admin main dashboard/navigation hub */}
                    <Route path="/admin/dashboard" element={<AdminDashboardPage />} />
                    {/* Page for Admins to approve pending CUSTOMER registrations */}
                    <Route path="/admin/pending-customers" element={<PendingApprovalsPage />} /> {/* Renamed for clarity */}
                    {/* *** NEW: Page for Admins to list/manage JOB applications *** */}
                    <Route path="/admin/applications" element={<AdminApplicationsListPage />} />
                    {/* *** NEW: Page for Admins to view details and action a specific JOB application *** */}
                    <Route path="/admin/applications/:appId" element={<AdminApplicationDetailPage />} />
                    {/* Note: Admins also gain access to Employee Routes via the block below */}
                </Route>


                {/* ======================================== */}
                {/*           Employee Routes                */}
                {/* Requires ROLE_EMPLOYEE or ROLE_ADMIN    */}
                {/* ======================================== */}
                {/* This ProtectedRoute block ensures both Employees and Admins can access these */}
                <Route element={<ProtectedRoute allowedRoles={['ROLE_EMPLOYEE', 'ROLE_ADMIN']} />}>
                    {/* Employee main dashboard/navigation hub */}
                    <Route path="/employee/dashboard" element={<EmployeeDashboardPage />} />
                    {/* Employee actions (performing FOR a customer) */}
                    <Route path="/employee/deposit" element={<EmployeeDepositPage />} />
                    <Route path="/employee/withdraw" element={<EmployeeWithdrawalPage />} />
                    <Route path="/employee/check-balance" element={<EmployeeCheckBalancePage />} />
                    <Route path="/employee/download-history" element={<EmployeeDownloadHistoryPage />} />
                    {/* Add other employee routes here (e.g., search customer) */}
                </Route>


                {/* ======================================== */}
                {/*             Cleanup                      */}
                {/* Remove old/conflicting routes           */}
                {/* ======================================== */}
                {/* REMOVE Placeholder public route for employee */}
                {/* <Route path="/employee" element={<Employee_Dash />} /> */}
                {/* REMOVE Old Public /admin route if it exists */}
                {/* <Route path="/admin" element={<Admin_Dash />} /> */}


                {/* ======================================== */}
                {/*         404 Not Found Route            */}
                {/* Must be the LAST route definition       */}
                {/* ======================================== */}
                <Route path="*" element={<div style={{ padding: '40px', textAlign: 'center' }}><h2>404 - Page Not Found</h2><p>The page you requested could not be found.</p></div>} />

            </Routes>
        </Router>
    );
}

export default App;
