import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './component/common/Navbar.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './component/pages/Home';
import Customer_Dash from './component/Page/Customer_Dash.js';
import Employee_Dash from './component/Page/Employee_Dash.js';
import Admin_Dash from './component/Page/Admin_Dash.js';

// Main App Component
function App() {
  const location = useLocation(); // Get the current route

  // Inline style for the logo
  const logoStyle = {
    display: 'block',
    margin: 'auto',
    marginTop: '50px',
    width: '150px', // Adjust size as needed
  };

  return (
    <Router>
      {/* Conditionally render the Navbar or only logo */}
      {location.pathname !== '/customer' && (
        <Navbar />
      )}

      {/* Display the logo above the customer dashboard page */}
      {location.pathname === '/customer' && (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
          <img src="path_to_logo.png" alt="Mewo Bank Logo" style={logoStyle} />
        </div>
      )}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/customer" element={<Customer_Dash />} />
        <Route path="/employee" element={<Employee_Dash />} />
        <Route path="/admin" element={<Admin_Dash />} />
      </Routes>
    </Router>
  );
}

export default App;
