// --- src/component/Page/Admin/AdminApplicationsListPage.js ---
// ENHANCED VERSION

import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Link as RouterLink, useNavigate } from 'react-router-dom';

// Import MUI components
import {
    Container, Typography, Box, Button, Paper, Grid, Tooltip, Chip, IconButton,
    TableContainer, Table, TableHead, TableRow, TableCell, TableBody,
    CircularProgress, Alert, Link as MuiLink // Removed Snackbar as it's less needed here
} from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import VisibilityIcon from '@mui/icons-material/Visibility';
import ArrowBackIcon from '@mui/icons-material/ArrowBack'; // Icon for back button

const API_BASE_URL = 'http://localhost:8080';

// Helper function to format date/time (Keep as is)
const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return 'N/A';
    try {
        const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit', hour12: true };
        return new Intl.DateTimeFormat('en-US', options).format(new Date(dateTimeString));
    } catch (e) {
        console.error("Date formatting error:", e);
        return 'Invalid Date';
    }
};

// Helper function for status chips (Keep as is)
const getStatusChip = (status) => {
    let color = 'default';
    switch (status) {
        case 'PENDING': color = 'warning'; break;
        case 'UNDER_REVIEW': color = 'info'; break;
        case 'INTERVIEW_SCHEDULED': color = 'primary'; break;
        case 'HIRED': color = 'success'; break;
        case 'REJECTED': color = 'error'; break;
        default: color = 'default';
    }
    return <Chip label={status?.replace('_', ' ') ?? 'UNKNOWN'} color={color} size="small" variant="outlined" />; // Use outlined variant
};


export default function AdminApplicationsListPage() {
    const [applications, setApplications] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // Fetching logic (Keep as is, including logging from previous step)
    const fetchApplications = useCallback(async () => {
        setIsLoading(true);
        setError('');
        console.log("Attempting to fetch job applications...");
        try {
            const response = await axios.get(`${API_BASE_URL}/admin/applications`, {
                withCredentials: true
            });
            console.log("Fetched applications response:", response);
             if (Array.isArray(response.data)) {
                 console.log("Setting applications state with data:", response.data);
                 setApplications(response.data);
            } else {
                 console.error("Received non-array data:", response.data);
                 setError("Received unexpected data format from server.");
                 setApplications([]);
            }
        } catch (err) {
             console.error("Error fetching applications:", err);
            if(err.response) {
                console.error("Error response data:", err.response.data);
                console.error("Error response status:", err.response.status);
            }
            setError(err.response?.data?.message || err.message || 'Failed to fetch job applications.');
            setApplications([]);
        } finally {
            setIsLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchApplications();
    }, [fetchApplications]);

    const handleViewDetails = (appId) => {
        navigate(`/admin/applications/${appId}`);
    };

    return (
        <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}> {/* Use wider container if needed */}

             {/* Styled Header */}
            <Paper elevation={4} sx={{
                p: 2,
                mb: 3, // Margin bottom
                textAlign: 'center',
                background: 'linear-gradient(45deg, #ff9800 30%, #f57c00 90%)', // Example Orange Gradient
                color: 'white',
                borderRadius: '8px'
            }}>
                 <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold' }}>
                    Manage Job Applications
                </Typography>
            </Paper>

             {/* Main Content Paper */}
             <Paper elevation={2} sx={{ p: { xs: 1, sm: 2, md: 3 }, borderRadius: '8px' }}>
                 {/* Top Bar: Back Button & Refresh */}
                 <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2, borderBottom: '1px solid #eee', pb: 1 }}>
                     <Button
                        variant="outlined"
                        color="primary"
                        size="small"
                        startIcon={<ArrowBackIcon />}
                        onClick={() => navigate('/admin/dashboard')} // Link back to admin dashboard
                    >
                        Admin Dashboard
                    </Button>
                    <Tooltip title="Refresh List">
                        <span>
                            <IconButton onClick={fetchApplications} color="primary" disabled={isLoading}>
                                <RefreshIcon />
                            </IconButton>
                        </span>
                    </Tooltip>
                 </Box>

                {/* Loading State */}
                {isLoading && (
                    <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', my: 5, minHeight: '300px' }}>
                        <CircularProgress size={50} />
                        <Typography variant="h6" color="text.secondary" sx={{mt: 2}}>Loading Applications...</Typography>
                    </Box>
                )}

                {/* Error State */}
                {!isLoading && error && (
                    <Box sx={{ my: 3 }}>
                        <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>
                        <Button onClick={fetchApplications} variant="outlined">Try Again</Button>
                    </Box>
                )}

                {/* Table Display */}
                {!isLoading && !error && (
                    <TableContainer component={Paper} variant="outlined" > {/* Use outlined Paper */}
                        <Table stickyHeader aria-label="job applications table">
                            <TableHead>
                                <TableRow sx={{ '& th': { fontWeight: 'bold', backgroundColor: 'secondary.light', color: 'common.black' } }}> {/* Lighter secondary header */}
                                    <TableCell>Applicant Name</TableCell>
                                    <TableCell>Email</TableCell>
                                    <TableCell>Phone</TableCell>
                                    <TableCell>Desired Role</TableCell>
                                    <TableCell>Applied On</TableCell>
                                    <TableCell align="center">Status</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {applications.length === 0 ? (
                                    <TableRow>
                                        <TableCell colSpan={7} align="center" sx={{ py: 5, fontStyle: 'italic', color: 'text.secondary' }}> {/* Increased padding */}
                                            No submitted job applications found.
                                            </TableCell>
                                    </TableRow>
                                ) : (
                                    applications.map((app, index) => (
                                        <TableRow
                                            hover
                                            key={app.id}
                                            sx={{
                                                '&:last-child td, &:last-child th': { border: 0 },
                                                backgroundColor: index % 2 !== 0 ? 'action.hover' : 'background.paper' // Alternate row style
                                             }}
                                        >
                                            <TableCell sx={{fontSize: '0.875rem'}}>{`${app.applicantFirstName || ''} ${app.applicantLastName || ''}`.trim()}</TableCell>
                                            <TableCell sx={{fontSize: '0.875rem'}}>{app.applicantEmail}</TableCell>
                                            <TableCell sx={{fontSize: '0.875rem'}}>{app.applicantPhone}</TableCell>
                                            <TableCell sx={{fontSize: '0.875rem'}}>{app.desiredRole || 'N/A'}</TableCell>
                                            <TableCell sx={{fontSize: '0.875rem'}}>{formatDateTime(app.applicationDate)}</TableCell>
                                            <TableCell align="center">{getStatusChip(app.status)}</TableCell>
                                            <TableCell align="center">
                                                <Tooltip title="View Details & Actions">
                                                    <IconButton
                                                        color="primary"
                                                        size="small"
                                                        onClick={() => handleViewDetails(app.id)}
                                                    >
                                                        <VisibilityIcon />
                                                    </IconButton>
                                                </Tooltip>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                )}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}
            </Paper> {/* End Content Paper */}
        </Container>
    );
}
