// --- src/component/Page/Admin/AdminDashboardPage.js ---
// MODIFIED based on your provided code

import React from 'react'; // Removed unused state hooks and axios (unless needed for other future dashboard elements)
// Import MUI components
import {
    Container, Typography, Box, Button, Paper, Grid, Card, CardContent, CardActions
} from '@mui/material';
// Import MUI Icons
import PeopleAltIcon from '@mui/icons-material/PeopleAlt';
import WorkOutlineIcon from '@mui/icons-material/WorkOutline';
// Import React Router Link
import { Link as RouterLink } from 'react-router-dom';

export default function AdminDashboardPage() {
    // Removed all state and functions related to fetching/displaying pending customers here.
    // That logic now belongs in PendingApprovalsPage.js

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            {/* Dashboard Header */}
            <Paper elevation={3} sx={{ p: { xs: 2, md: 3 }, mb: 4, textAlign: 'center' }}>
                <Typography variant="h4" component="h1" gutterBottom>
                    Admin Dashboard
                </Typography>
                <Typography variant="body1" color="text.secondary">
                    Manage customer accounts and job applications from here.
                </Typography>
            </Paper>

            {/* Action Cards Grid */}
            <Grid container spacing={4} justifyContent="center"> {/* Increased spacing */}

                {/* Card 1: Customer Approvals */}
                <Grid item xs={12} sm={6} md={5}> {/* Adjusted grid size */}
                    <Card sx={{ display: 'flex', flexDirection: 'column', height: '100%', '&:hover': { boxShadow: 6 }, transition: 'box-shadow 0.3s' }}>
                        <CardContent sx={{ flexGrow: 1, textAlign: 'center', p: 3 }}>
                            <PeopleAltIcon color="primary" sx={{ fontSize: 60, mb: 2 }} />
                            <Typography gutterBottom variant="h5" component="h2">
                                Customer Approvals
                            </Typography>
                            <Typography variant="body2" color="text.secondary" sx={{ minHeight: '40px' }}> {/* Added min height for alignment */}
                                View and manage pending customer registrations.
                            </Typography>
                        </CardContent>
                        <CardActions sx={{ justifyContent: 'center', pb: 3 }}>
                            {/* Use RouterLink for navigation */}
                            <Button
                                component={RouterLink}
                                // Ensure this path matches your controller and App.js route
                                to="/admin/pending-customers"
                                size="medium"
                                variant="contained"
                                color="primary"
                            >
                                Manage Customers
                            </Button>
                        </CardActions>
                    </Card>
                </Grid>

                {/* Card 2: Job Applications */}
                <Grid item xs={12} sm={6} md={5}> {/* Adjusted grid size */}
                    <Card sx={{ display: 'flex', flexDirection: 'column', height: '100%', '&:hover': { boxShadow: 6 }, transition: 'box-shadow 0.3s' }}>
                        <CardContent sx={{ flexGrow: 1, textAlign: 'center', p: 3 }}>
                            <WorkOutlineIcon color="secondary" sx={{ fontSize: 60, mb: 2 }} />
                            <Typography gutterBottom variant="h5" component="h2">
                                Job Applications
                            </Typography>
                            <Typography variant="body2" color="text.secondary" sx={{ minHeight: '40px' }}> {/* Added min height for alignment */}
                                Review submitted job applications and manage hiring process.
                            </Typography>
                        </CardContent>
                        <CardActions sx={{ justifyContent: 'center', pb: 3 }}>
                            {/* Use RouterLink for navigation */}
                            <Button
                                component={RouterLink}
                                to="/admin/applications" // Link to the new applications list page
                                size="medium"
                                variant="contained"
                                color="secondary"
                            >
                                Manage Applications
                            </Button>
                        </CardActions>
                    </Card>
                </Grid>

                {/* Add more cards here for other admin functions as needed */}

            </Grid>

            {/* The TableContainer and Snackbar related to pending customers have been removed. */}
            {/* They belong in the dedicated PendingApprovalsPage component. */}

        </Container>
    );
}
