// --- src/pages/Admin/AdminDashboard.js ---
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios'; // Import axios for API calls
// Import MUI components for layout, table, buttons, feedback
import {
    Container, Typography, Box, Button, Paper, Grid,
    TableContainer, Table, TableHead, TableRow, TableCell, TableBody,
    CircularProgress, Alert, Snackbar, IconButton, Tooltip
} from '@mui/material';
// Import MUI Icons
import CheckCircleIcon from '@mui/icons-material/CheckCircle'; // Approve icon
import CancelIcon from '@mui/icons-material/Cancel'; // Reject icon
import RefreshIcon from '@mui/icons-material/Refresh'; // Refresh icon

// Define your backend API base URL
const API_BASE_URL = 'http://localhost:8080'; // Adjust if different

export default function AdminDashboardPage() { // Renamed component
    const [pendingUsers, setPendingUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [snackbarOpen, setSnackbarOpen] = useState(false);

    // --- Function to Fetch Pending Users ---
    // useCallback helps prevent unnecessary re-renders if passed as prop or used in useEffect dependency
    const fetchPendingUsers = useCallback(async () => {
        setIsLoading(true);
        setError(''); // Clear previous errors
        setSuccessMessage(''); // Clear previous success messages
        try {
            // Make API call to backend GET /admin/pending
            // Requires authentication (e.g., Basic Auth headers or JWT) handled by axios config or interceptors
            const response = await axios.get(`${API_BASE_URL}/admin/pending`, {
                 withCredentials: true // Crucial for sending session cookies or Basic Auth headers if CORS allows
            });
            setPendingUsers(response.data || []); // Ensure response.data is an array
        } catch (err) {
            console.error("Error fetching pending users:", err);
            // Set a user-friendly error message
            setError(err.response?.data || err.message || 'Failed to fetch pending registrations. Ensure you are logged in as Admin.');
            setPendingUsers([]); // Clear user list on error
        } finally {
            setIsLoading(false);
        }
    }, []); // useCallback dependency array is empty as it doesn't depend on props/state here

    // --- Fetch users when the component mounts ---
    useEffect(() => {
        fetchPendingUsers();
    }, [fetchPendingUsers]); // Run effect when fetchPendingUsers function reference changes (only on mount)

    // --- Handler for Snackbar Close ---
    const handleSnackbarClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setSnackbarOpen(false);
    };

    // --- Show Snackbar with Message ---
    const showMessage = (message, isSuccess = true) => {
        if (isSuccess) {
            setSuccessMessage(message);
            setError(''); // Clear any previous errors
        } else {
            setError(message);
            setSuccessMessage(''); // Clear any previous success
        }
        setSnackbarOpen(true); // Open the snackbar
    };

    // --- Handler for Approving a user ---
    const handleApprove = async (customerId) => {
        try {
            // Make API call to POST /admin/approve/{customerId}
            // Requires authentication headers
            const response = await axios.post(`${API_BASE_URL}/admin/approve/${customerId}`, null, { // No request body
                 withCredentials: true
            });
            showMessage(response.data || `User ${customerId} approved successfully!`); // Show success
            fetchPendingUsers(); // Refresh the list
        } catch (err) {
            console.error(`Error approving user ${customerId}:`, err);
            showMessage(err.response?.data || err.message || `Failed to approve user ${customerId}.`, false); // Show error
        }
    };

    // --- Handler for Rejecting a user ---
    const handleReject = async (customerId) => {
        // Optional: Confirmation dialog
        if (!window.confirm(`Are you sure you want to reject and remove user ${customerId}? This cannot be undone.`)) {
            return;
        }
        try {
            // Make API call to POST /admin/reject/{customerId}
            // Requires authentication headers
            const response = await axios.post(`${API_BASE_URL}/admin/reject/${customerId}`, null, { // No request body
                 withCredentials: true
            });
            showMessage(response.data || `User ${customerId} rejected successfully!`); // Show success
            fetchPendingUsers(); // Refresh the list
        } catch (err) {
            console.error(`Error rejecting user ${customerId}:`, err);
            showMessage(err.response?.data || err.message || `Failed to reject user ${customerId}.`, false); // Show error
        }
    };

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            {/* Dashboard Header */}
            <Paper elevation={3} sx={{ p: { xs: 2, md: 3 }, mb: 3 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap' }}>
                    <Typography variant="h4" component="h1" gutterBottom>
                        Admin Dashboard
                    </Typography>
                    {/* Add other summary info or quick links here if needed */}
                </Box>
            </Paper>

            {/* Pending Approvals Section */}
            <Paper elevation={3} sx={{ p: { xs: 2, md: 3 } }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                    <Typography variant="h5" component="h2">
                        Pending Registrations
                    </Typography>
                    <Tooltip title="Refresh List">
                        <IconButton onClick={fetchPendingUsers} color="primary" disabled={isLoading}>
                            <RefreshIcon />
                        </IconButton>
                    </Tooltip>
                </Box>

                {/* Loading Indicator */}
                {isLoading && (
                    <Box sx={{ display: 'flex', justifyContent: 'center', my: 5 }}>
                        <CircularProgress />
                    </Box>
                )}

                {/* Error Alert */}
                {!isLoading && error && (
                    <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>
                )}

                {/* Table of Pending Users */}
                {!isLoading && !error && (
                    <TableContainer component={Paper} elevation={1} sx={{ maxHeight: 600 }}>
                        <Table stickyHeader aria-label="pending users table">
                            <TableHead>
                                <TableRow sx={{ '& th': { fontWeight: 'bold', backgroundColor: 'primary.light', color: 'white' } }}>
                                    <TableCell>Customer ID</TableCell>
                                    <TableCell>Name</TableCell>
                                    <TableCell>Email</TableCell>
                                    <TableCell>Mobile</TableCell>
                                    <TableCell>Account No</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {pendingUsers.length === 0 ? (
                                     <TableRow>
                                         <TableCell colSpan={6} align="center" sx={{ py: 3 }}>No pending registrations found.</TableCell>
                                     </TableRow>
                                ) : (
                                    pendingUsers.map((user) => (
                                        <TableRow
                                            hover
                                            key={user.customerId}
                                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                        >
                                            <TableCell component="th" scope="row">{user.customerId}</TableCell>
                                            <TableCell>{`${user.fname || ''} ${user.lname || ''}`.trim()}</TableCell>
                                            <TableCell>{user.email}</TableCell>
                                            <TableCell>{user.mobileNumber}</TableCell>
                                            <TableCell>{user.accountNumber}</TableCell>
                                            <TableCell align="center">
                                                <Tooltip title="Approve User">
                                                    <IconButton
                                                        color="success"
                                                        size="small"
                                                        onClick={() => handleApprove(user.customerId)}
                                                        sx={{ mr: 0.5 }}
                                                    >
                                                        <CheckCircleIcon />
                                                    </IconButton>
                                                </Tooltip>
                                                 <Tooltip title="Reject User">
                                                    <IconButton
                                                        color="error"
                                                        size="small"
                                                        onClick={() => handleReject(user.customerId)}
                                                    >
                                                        <CancelIcon />
                                                    </IconButton>
                                                </Tooltip>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                )}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}
            </Paper>

            {/* Snackbar for Success/Error Messages */}
             <Snackbar
                open={snackbarOpen}
                autoHideDuration={5000} // Display for 5 seconds
                onClose={handleSnackbarClose}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
             >
                {/* Render Alert inside Snackbar for styling */}
                <Alert onClose={handleSnackbarClose} severity={successMessage ? "success" : "error"} sx={{ width: '100%' }}>
                    {successMessage || error}
                </Alert>
            </Snackbar>

             {/* Add sections/links for other admin functionalities here */}
             {/* Example:
             <Paper elevation={3} sx={{ p: 3, mt: 3 }}>
                 <Typography variant="h5" component="h2" gutterBottom>Other Actions</Typography>
                 <Button variant="outlined" sx={{ mr: 2 }}>Manage All Customers (Future)</Button>
                 <Button variant="outlined">Create Employee (Future)</Button>
             </Paper>
             */}

        </Container>
    );
}
